import java.util.ArrayList;
class ArrayListUtilization {
    public static void main(String[] args) {
        ArrayList<Integer> myList = new ArrayList<>(3);
        myList.add(3);
        myList.add(2);
        myList.add(1);
        System.out.println(myList);
    }
}

// output: myList = [3, 2, 1]

