class Rectangle {
  private _width: number;
  private _height: number;

  constructor(width: number, height: number) {
    this._width = width;
    this._height = height;
  }

  area() {
    return this._width * this._height;
  }
}

class Window {
  private _bounds: Rectangle;

  constructor(bounds: Rectangle) {
    this._bounds = bounds;
  }

  area() {
    return this._bounds.area();
  }
}

