def partition(arr, first, last):
	pivotValue = arr[first]
	leftmark = first+1
	rightmark = last

	done = False

	while not done:
		while leftmark <= rightmark and arr[leftmark] <= pivotValue:
			leftmark += 1
		while arr[rightmark] >= pivotValue and rightmark >= leftmark:
			rightmark -= 1
		if rightmark < leftmark:
