import List from 'immutable-ext'
import {Either} from '../types'
const {Left, Right} = Either

const isPresent = x => !!x

const validate = (spec, obj) => {
  List.(Object.keys(spec)).foldMap(key => {
    spec[key](obj[key]) ? Right(obj) : Left(`${key} bad`)
  }, Either.of(obj))
}

