  /**
   * (node) => // do something with node
   */
  traverseBFS(fn) {
    // start at root
    // check if children
    // if children, iterate through and recall function
    const arr = [this.root];
    while (arr.length) {
      const node = arr.shift();
      arr.push(...node.children);
      fn(node);
    }
  }

  traverseDFS(fn) {
    // start with root
    // check if children
    // if children, iterate through in depth fashion
    const arr = [this.root];
    while (arr.length) {
      const node = arr.shift();
      arr.unshift(...node.children);
      fn(node);
    }
  }
