  return { major, minor, patch };
}

// the plugin
export default devPlugin;

function devPlugin({ types: t }) {
  return {
    name: 'line-plugin',
    visitor: {
      Identifier(path) {
        const {
          node: { name },
        } = path;
        if (name === '__DEV__') {
          path.replaceWith(
            t.booleanLiteral(process.env.NODE_ENV === 'development'),
          );
        }
      },
    },
  };
}

// the tests
import { stripIndent } from 'common-tags';
import * as babel from 'babel-core';
import devPlugin from './05_babel';

const env = process.env.NODE_ENV;

afterEach(() => {
  process.env.NODE_ENV = env;
});

test('transpiles __DEV__ to false', () => {
  process.env.NODE_ENV = 'production';
  const source = stripIndent`
    if (__DEV__) {
      console.log('You are in dev mode!')
    }
  `;
  const { code } = babel.transform(source, {
    babelrc: false,
    plugins: [devPlugin],
  });
  expect(code).not.toContain('__DEV__');
  expect(code).toMatchSnapshot();
});

test('transpiles __DEV__ to true', () => {
  process.env.NODE_ENV = 'development';
  const source = stripIndent`
    if (__DEV__) {
      console.log('You are in dev mode!')
    }
  `;
  const { code } = babel.transform(source, {
    babelrc: false,
    plugins: [devPlugin],
  });
  expect(code).not.toContain('__DEV__');
  expect(code).toMatchSnapshot();
});

// Press ctrl+space for code completion
export default function transformer(file, api) {
  const j = api.jscodeshift;

  return j(file.source)
    .find(j.Identifier)
    .forEach(path => {
      j(path).replaceWith(
        j.identifier(
          path.node.name
            .split('')
            .reverse()
            .join(''),
        ),
      );
    })
    .toSource();
}

