function PalindromeTwo(str) {
  // code goes here
  return (
    str.replace(/[^a-zA-Z0-9]/gi, '').toLowerCase() ===
    str
      .split('')
      .reverse()
      .join('')
      .replace(/[^a-zA-Z0-9]/gi, '')
      .toLowerCase()
  );
}

